import {useState} from "react";
import axios from 'axios';
import {useNavigate} from "react-router-dom";

const AuthModal = ({setShowModal,isSignUp}) => {

    const [email, setEmail] = useState(null)
    const [password, setPassword] = useState(null)
    const [confirmPassword, setConfirmPassword] = useState(null)
    const [error, setError] = useState(null)

    let navigate = useNavigate()

    console.log(email, password, confirmPassword)

    const handleClick = () => {
        setShowModal(false)
    }

    const handleSubmit = async (e) =>{
        e.preventDefault()
        try{
            if( isSignUp && (password !== confirmPassword)){
                setError("Passwords need to match!")
                return
            }

            const response = await axios.post('http://localhost:8080/signup',{email, password})

            // 201ÂìçÂ∫îÁ†ÅÊÑèÂë≥ÁùÄÊàêÂäüËØ∑Ê±ÇÂπ∂ÂàõÂª∫Êñ∞ÁöÑËµÑÊ∫ê https://zhuanlan.zhihu.com/p/462030879
            const success = response.status == 201
            if (success) navigate('/onboarding')
        }catch(error){
            console.log(error)
        }
    }


//Á¨¨‰∏â‰∏™inputÂâçÈù¢ÊúâisSignUPÁöÑÊù°‰ª∂ÔºåÂõ†‰∏∫ÁôªÈôÜÂêé‰∏çÈúÄË¶ÅÂØÜÁ†Å‰∫åÊ¨°Á°ÆËÆ§ÔºåÊâÄ‰ª•Âä†‰∏Ä‰∏™Âà§Êñ≠ÔºåÂ¶ÇÊûúÊòØÁôªÈôÜÔºåÂ∞±‰∏çÈúÄË¶ÅËøô‰∏Ä‰∏™input
    return (
        <div className="auth-modal">
            <div className="close-icon" onClick={handleClick}>üôÖ</div>
            <h2>{isSignUp ? 'CREATE ACCOUNT' : 'LOG IN'}</h2>
            <p>By CLicking Log In, you agree to our terms. Learn how we process your data in Privacy Policy</p>
            <form onSubmit={handleSubmit}>
                <input
                    type="email"
                    id="email"
                    name="email"
                    placeholder="email"
                    required={true}
                    onCharge={(e) => setEmail(e.target.value)}
                />

                <input
                    type="password"
                    id="password"
                    name="password"
                    placeholder="password"
                    required={true}
                    onCharge={(e) => setPassword(e.target.value)}
                />

                {isSignUp && <input
                    type="password"
                    id="password-check"
                    name="password-check"
                    placeholder="confirm password"
                    required={true}
                    onCharge={(e) => setConfirmPassword(e.target.value)}
                />}



                <input className="secondary-button" type="submit" value="Submit"/>
                <p>{error}</p>
            </form>
            <hr/>
            <h2>GET THE APP</h2>

        </div>
    )
}
export default AuthModal
