import {useState} from "react";

const AuthModal = ({setShowModal}) => {

    const [email, setEmail] = useState(null)
    const [password, setPassword] = useState(null)
    const [confirmPasswprd, setConfirmPassword] = useState(null)
    const [error, setError] = useState(null)

    console.log(email, password, confirmPasswprd)

    const handleClick = () => {
        setShowModal(false)
    }

    const handleSubmit = (e) =>{
        e.preventDefault()
    }

    const isSignUp = true

    return (
        <div className="auth-modal">
            <div className="close-icon" onClick={handleClick}>ðŸ™…</div>
            <h2>{isSignUp ? 'CREATE ACCOUNT' : 'LOG IN'}</h2>
            <p>By CLicking Log In, you agree to our terms. Learn how we process your data in Privacy Policy</p>
            <form onSubmit={handleSubmit}>
                <input
                    type="email"
                    id="email"
                    name="email"
                    placeholder="email"
                    required={true}
                    onCharge={(e) => setEmail(e.target.value)}
                />

                <input
                    type="password"
                    id="password"
                    name="password"
                    placeholder="password"
                    required={true}
                    onCharge={(e) => setPassword(e.target.value)}
                />

                <input
                    type="password-check"
                    id="password-check"
                    name="password-check"
                    placeholder="confirm password"
                    required={true}
                    onCharge={(e) => setConfirmPassword(e.target.value)}
                />
            </form>

            AUTH MODAL
        </div>
    )
}
export default AuthModal
